import React from 'react';
import './App.css';
import { Person } from './Person/Person.js'

class App extends React.Component{
  state = {
    persons: [
      {name: 'Max', age: 28},
      {name: 'Manu', age: 29},
      {name: 'Steff', age: 26}

    ]
  }

  switchNameHandler = (newName) => {
    this.setState(
      {
        persons: [
          {name: newName , age: 28},
          {name: 'Manu', age: 29},
          {name: 'Steff', age: 30}
        ]
      }
    )
  }

  nameChangedHandler = (e) =>{
    this.setState(
      {
        persons: [
          {name: e.target.value , age: 28},
          {name: 'Manu', age: 29},
          {name: 'Steff', age: 30}
        ]
      }
    )
  }


  render(){
    return(
      <div className="App">
        <h1>Hello World</h1>
        <button onClick={this.switchNameHandler.bind(this, 'Bob')}>Switch</button>
        <Person name={this.state.persons[0].name} 
                age={this.state.persons[0].age} 
                change = {this.nameChangedHandler}/>
        <Person name={this.state.persons[1].name} 
                age={this.state.persons[1].age} > My hobbies are soccer </Person>
        <Person name={this.state.persons[2].name} 
                age={this.state.persons[2].age} />
      </div>
    )
  }
}

export default App;
