import React from 'react';
import './App.css';
import {Validation} from './ValidationComponent/ValidationComponent.js';



class App extends React.Component{

    state = {
      length: 0
    }

    lengthHandler = (event) =>{
      let wordlength = event.target.value.length
      this.setState({
        length: wordlength
      })
    }



    render(){

      let validation = null
      if (this.state.length <= 5){
        validation = "Text too short"
      }
      else {
        validation = "Text too long"
      }

      return(
        <div>
          <input type="text" onChange = {this.lengthHandler} />
          <Validation textlength = {this.state.length} validation = {validation}  />
        </div>
      )
    }

}


export default App
