import React from 'react';
import './App.css';
import {Validation} from './ValidationComponent/ValidationComponent.js';
import {Char} from './Char/Char.js';



class App extends React.Component{

    state = {
      length: 0,
      userInput: ''
    }

    lengthHandler = (event) =>{
      let wordlength = event.target.value.length
      this.setState({
        length: wordlength,
        userInput: event.target.value
      })
    }

    removeChar = (index) => {
      const text = this.state.userInput.split('')
      text.splice(index, 1)
      const updatedText = text.join('')
    }

    render(){

      const charList = this.state.userInput.split('').map((char, index) => {
        return <Char character = {char} key = {index} click = {this.removeChar(index)} />
      })

      let validation = null
      if (this.state.length <= 5){
        validation = "Text too short"
      }
      else {
        validation = "Text too long"
      }

      return(
        <div>
          <input type="text" onChange = {this.lengthHandler} />
          <p>{this.state.userInput}</p>
          <Validation textlength = {this.state.length} validation = {validation}  />
          {charList}
        </div>
      )
    }

}


export default App
