import React from 'react'
import classes from './Modal.module.css'
import Backdrop from '../Backdrop/Backdrop.js'

const Modal = (props) => {
    let css
    if (props.showModal){
        css = classes.Modal1
    }
    else{
        css = classes.Modal
    }
    return(
        <React.Fragment>
            <Backdrop showModal = {props.showModal}/>
            <div className = {css}>
                {props.children}
            </div>
        </React.Fragment>
    )
}
    


export default Modal
