import React from 'react';
import './App.css';

class App extends React.Component{
  style = {
    backgroundColor: 'green',
    color: 'white',
    border: '1px solid blue',
    padding: '8px',
    cursor: 'pointer',
  }
  clickHandler = () => {
    this.style.backgroundColor = 'red'
  }
  render(){


    return(
      <button style={this.style} onClick={this.clickHandler}>Click Me</button>
    )
  }
}
export default App
