import React from 'react'

export const Person = (props) => {
    return(
        <div className="Person">
            <p>My name is {props.name} and i am {props.age} years old</p>
            <p>{props.children}</p>
            <input type="text" onChange = {props.change}/>
        </div>
        
    )
}
