import React from 'react'
import classes from './Modal.module.css'

const Modal = (props) => {
    let css
    if (props.showModal){
        css = classes.Modal1
    }
    else{
        css = classes.Modal
    }
    return(
        <div className = {css}>
            {props.children}
        </div>
    )
}
    


export default Modal
