import React from 'react';
import './App.css';
import {Validation} from './ValidationComponent/ValidationComponent.js';



class App extends React.Component{

    state = {
      length: 0
    }

    lengthHandler = (event) =>{
      let wordlength = event.target.value.length
      this.setState({
        length: wordlength
      })
    }

    render(){
      return(
        <div>
          <input type="text" onChange = {this.lengthHandler} />
          <Validation textlength = {this.state.length}  />
        </div>
      )
    }

}


export default App
