import React from 'react'
import classes from './OrderSummary.module.css'
import Button from '../../UI/Button/Button.js'

const OrderSummary = (props) => {
    const ingredientSummary = Object.keys(props.ingredients).map(ing => {
        if (props.ingredients[ing] !== 0){
            return <li key={ing}>{ing}: {props.ingredients[ing]}</li>
        }
    })
    return(
        <React.Fragment>
            <h3>Your Order</h3>
            <p>Delicious Burger with the following ingredients</p>
            <ul>
                {ingredientSummary}
            </ul>
            <p>Continue to Check Out?</p>
            <Button clicked = {props.click} btnType="Success">Continue</Button>
            <Button clicked = {props.click} btnType="Danger">Continue</Button>
        </React.Fragment>
    )
}

export default OrderSummary
